body{font-family: "Avenir Next",Helvetica,Arial,"Lantinghei SC","Microsoft YaHei",sans-serif;font-size:15px;font-weight:bold;color:#505050}
::-moz-selection{color:#fff}
::selection{color:#fff}
a{text-decoration:none}
a:focus,a:hover{outline:0;text-decoration:underline}
.navbar{border-bottom:1px solid #eee;background:#fff}
.navbar-header h1,.navbar-header h2{margin:12px 0;padding:0 15px;font-size:22px;font-weight:100}
.navbar-header img{margin:6px 0;padding:0 15px;height:36px}
.navbar-toggle{margin:6px 10px}
.navbar-toggle:hover{background:#f9f9f9}
.navbar-nav li{margin:0;padding:0}
.navbar-nav li a:hover{background:#f9f9f9}
@media (min-width:768px){
    .navbar{height:100px}
    .navbar-header h1,.navbar-header h2{margin:30px 0;padding:0;font-size:35px}
    .navbar-header img{margin:19px 0;padding:0;height:60px}
}
.search{border:1px solid #eee;margin:25px 0;padding:0}
.search:hover{border:1px solid #ddd}
.search .search-input{border:none;padding:0 15px;width:88%;height:40px;line-height:40px;font-size:14px;color:#666;-webkit-transition:.3s;-moz-transition:.3s;transition:.3s}
.search .search-input:focus{width:88%}
.search .search-btn{border:none;margin-top:9px;margin-right:calc(2%);padding:0;width:21px;height:21px;cursor:pointer;background:url('../search.png')}
@media (min-width:768px){
    .search{margin:28px 0}
    .search .search-input{border:none;width:226px}
    .search .search-input:focus{width:270px}
    .search .search-btn{margin-right:10px}
}
.breadcrumbs{margin:5px 0}
.breadcrumbs span{padding:0 7px}
@media (min-width:768px){
    .breadcrumbs{margin:15px 0}
}
.multi{border-bottom:1px solid #eee;padding:15px 0;font-size:20px;font-weight:100}
.post-title{margin:25px 0 10px 0;line-height:30px;font-size:18px;font-weight:bold}
.post-info{margin:0 0 20px 0;font-size:14px;color:#666}
.line{border-bottom:1px solid #eee;padding:0 0 20px 0;font-weight:100}
.single-title{margin:35px 0 10px 0;line-height:30px;font-size:25px;font-weight:100}
.single-info{margin:0 0 30px 0;font-size:14px;color:#666}
.single-tag{margin-top:60px;line-height:30px}
.prev-next{overflow:hidden;margin-top:20px;line-height:30px;white-space:nowrap;text-overflow:ellipsis}
.single-tit{border-bottom:1px solid #eee;padding:35px 0 11px 0;font-size:18px;font-weight:100}
.single-tit a{padding:0 0 0 20px;font-size:14px}
.mutuality ul{overflow:hidden;list-style:none;padding:10px 0 0 0}
.mutuality ul li{overflow:hidden;float:left;padding:10px 0 0 0;width:100%;white-space:nowrap;text-overflow:ellipsis}
@media (min-width:768px){
    .mutuality ul li{width:50%}
}
article p{padding:0 0 10px 0;line-height:26px}
.comments blockquote{border:none;position:relative;margin:15px 0 0 0;padding:0 0 0 65px;font-size:15px}
.comments figure{position:absolute;overflow:hidden;left:0;top:0;margin:0;padding:0}
.comments figure img{display:block;border:1px solid #f3f3f3;padding:4px;width:50px;height:50px}
.comments cite{font-style:normal;display:block;overflow:hidden}
.comments cite b{font-weight:100;color:#999}
.comments time{padding:0 0 0 10px;font-size:12px;color:#999}
.comments blockquote .revertcomment{padding:0 0 0 10px;font-size:14px;font-weight:100}
.comments q{display:block;quotes:none;padding:5px 0}
.comments blockquote blockquote{margin:15px 0 0 -60px;padding:12px 15px 7px 75px;background:#f9f9f9}
.comments blockquote blockquote figure{margin:12px 0 0 12px}
.comments blockquote blockquote cite b{margin:0 5px 0 0}
.comments blockquote blockquote dd figure{margin:0;padding:0}
@media (min-width:768px){
    .comments blockquote blockquote{margin:15px 0 0 0}
}
.post-comment dd{position:relative;margin-top:20px;padding:0}
.post-comment figure{overflow:hidden;position:absolute;float:left;width:80px;text-align:center;white-space:nowrap;text-overflow:ellipsis}
.post-comment figure b{padding:5px;font-size:14px;font-weight:100;color:#666}
.post-comment figure img{display:block;padding:4px;width:80px;height:80px;border:1px solid #f3f3f3}
.post-comment .comment-form{margin-left:-15px}
@media (min-width:768px){
    .post-comment .comment-form{margin-left:85px}
}
.comment-form input[type='text']{border:1px solid #eee;padding:0 15px;width:100%;height:44px;line-height:44px}
.comment-form textarea{border:1px solid #eee;padding:15px;width:100%;height:120px}
.comment-form input[type='text']:hover,.comment-form textarea:hover{border:1px solid #ddd}
.comment-form input[type='submit']{border:0;margin:0 15px;padding:0 40px;height:44px;line-height:44px;color:#fff}
.comment-form .input-group-addon{border-radius:0;border:1px solid #eee;border-left:0;background:#f6f6f6}
.post-info span,.single-info span,.single-tit span,.single-tag a{padding:0 10px 0 0}
.function_t{border-bottom:1px solid #eee;padding:35px 0 11px 0;font-size:18px;font-weight:100}
.function ul{overflow:hidden;list-style:none;padding:10px 0 0 0}
.function ul li{overflow:hidden;line-height:30px;font-size:14px;white-space:nowrap;text-overflow:ellipsis}
.function ul li ul{padding:0 0 0 15px}
.function div{overflow:hidden;list-style:none;padding:10px 0 0 0}
.function div li{overflow:hidden;line-height:30px;font-size:14px;white-space:nowrap;text-overflow:ellipsis}
.function form{margin:15px 0 0 0;padding:0}
.function form input[type='text']{border:1px solid #eee;margin:0;padding:0 10px;width:78%;height:35px;line-height:35px;font-size:14px;color:#666}
.function form input[type='text']:hover{border:1px solid #ddd}
.function form input[type='submit']{margin:0;padding:0;width:20%;height:35px;line-height:35px;color:#fff}
@media (min-width:768px){
    .function form input[type='text']{width:67%}
    .function form input[type='submit']{width:30%}
}
#divTags ul li,#divArchives ul li,#divAuthors ul li,#divFavorites ul li,#divLinkage ul li,#divLinkage a,#divStatistics ul li,#divMisc ul li{float:left;width:50%}
#divTags div li,#divArchives div li,#divAuthors div li,#divFavorites div li,#divLinkage div li,#divStatistics div li,#divMisc div li{float:left;width:50%}
#divContorPanel ul,#divContorPanel div{overflow:hidden;padding:12px 0 0 0;line-height:26px}
#divContorPanel ul span,#divContorPanel div span{display:inline;padding:0 5px 0 0}
table[id$='Calendar']{border:none;margin-top:10px;width:100%;text-align:center;color:#666}
table[id$='Calendar'] caption{margin:0;padding:15px 0 10px 0;font-size:16px;text-align:center}
table[id$='Calendar'] th{text-align:center;color:#666}
table[id$='Calendar'] td{padding:4px}
table[id$='Calendar'] td a{display:block}
.comment-pagebar{margin:30px 0 10px 0;padding:0;text-align:center}
.pagebar{margin:40px 0 0 0;padding:0;text-align:center}
.pagebar a,.comment-pagebar a{display:inline-block;border:1px solid #fff;margin:0 5px 10px 0;padding:0 12px;height:30px;line-height:30px;color:#666;background:#fff}
.pagebar a:focus,.pagebar a:hover,.comment-pagebar a:focus,.comment-pagebar a:hover{text-decoration:none}
.pagebar .now-page,.comment-pagebar .now-page{display:inline-block;margin:0 5px 0 0;padding:0 12px;height:30px;line-height:30px;background:#fff}
.pagebar .page-num,.comment-pagebar .page-num{margin:0 15px 0 0;color:#666}
footer{margin:150px 0 50px 0}
footer p{margin:0;padding:5px 0 0 0}
footer p a{padding:0 7px}
.error{margin:100px 0 50px 0}
.error .en{font-size:40px;font-weight:100}
.error .cn{font-size:18px;font-weight:100}
div.go-top{display:none;z-index:9999;border-radius:2px;position:fixed;bottom:10px;right:10px;width:36px;height:36px;cursor:pointer}
div.go-top div.arrow{margin:2px 8px;padding:0;font-size:18px;color:#fff}
@media (min-width:768px){
    div.go-top{bottom:50px;right:50px}
}
/*简介文字特效*/
#zoomintro{color: rgba(80, 80, 80, 0.6);font-weight:100;-webkit-transition:.3s;-moz-transition:.3s;transition:.3s;}
@media {#zoomintro:hover{border-top:30px border-bottom:30px solid blue;font-size:22px;color:#505050;font-weight:bold;}}
#h1zoom{text-decoration:none;font-size:30px;-webkit-transition:.5s;-moz-transition:.5s;transition:.5s;}
@media {#h1zoom:hover{padding-left:20px;}}
#board{
    background-color:white;
    margin:0;
}
#myCanvas{
    position:absolute;
}
/*style*/
::-moz-selection{background:#0099FF}
::selection{background:#0099FF}
a{color:#0099FF;font-size:20px;}
a:focus,a:hover{color:#0099FF}
.navbar-header h1,.navbar-header h2{color:#0099FF}
.icon-bar{background:#0099FF}
.post-title{color:#0099FF}
.single-title{color:#0099FF}
.single-tit small{color:#0099FF}
.mutuality ul li{color:#0099FF}
.comment-form input[type='submit']{background:#0099FF}
.comment-form input[type='submit']:hover{background:#f45531}
.function ul li{color:#0099FF}
.function div li{color:#0099FF}
.function form input[type='submit']{border:1px solid #0099FF;background:#0099FF}
.function form input[type='submit']:hover{border:1px solid #f45531;background:#f45531}
table[id$='Calendar'] td a{border:1px solid #0099FF}
.pagebar a:focus,.pagebar a:hover,.comment-pagebar a:focus,.comment-pagebar a:hover{border:1px solid #0099FF;color:#0099FF}
.pagebar .now-page,.comment-pagebar .now-page{border:1px solid #0099FF;color:#0099FF}
.error{color:#0099FF}
#lwlhitokoto {font-family:Calibri,'Open Sans',Arial,'Hiragino Sans GB','Microsoft YaHei','STHeiti','WenQuanYi Micro Hei',SimSun,sans-serif;font-size:30px;color:#040d14;}
div.go-top{background:#0099FF}
div.go-top:hover{background:#f45531}

