```java
private List<T> getFilteredChildren() {
    if (filteredChildren == null) {
        childrenLock.lock();
        try {
            if (filteredChildren == null) {
                filteredChildren = Collections.unmodifiableList(
                        new ArrayList<T>(getChildren()));
            }
        } finally {
            childrenLock.unlock();
        }
    }
    return filteredChildren;
}
